mapper.select:
SELECT sc.ID , sc.headquartersName, sc.companyName, sc.companyCode, DATE_FORMAT(sc.StartTime,'%Y-%m-%d') as StartTime, DATE_FORMAT(sc.EndTime,'%Y-%m-%d') as EndTime, (Case sc.Status when 1 then '启用' else '禁用' END) as StatusDesc, sc.Status, sa.EmployeeName, date_format(sc.CreateTime, '%Y-%m-%d %H:%i:%s') CreateTime, date_format(sc.EditTime, '%Y-%m-%d %H:%i:%s') EditTime, sc.companyAttr, ss.ProjectNames, ss.orgName, ss.orgType FROM s_company_info sc left join b_account sa on sa.ID=sc.Creator left join (select GROUP_CONCAT(bp.ProjectName) as ProjectNames, GROUP_CONCAT(org.orgName) AS orgName, GROUP_CONCAT(org.orgType) AS orgType, org.OrgCompanyID from s_organization org inner join b_project bp on bp.ID=org.ProjectID and OrgCompanyID is not null GROUP BY OrgCompanyID) ss on ss.OrgCompanyID=sc.ID WHERE sc.IsDel = 0
and sc.companyName like '%${companyName}%'
and sc.headquartersName like '%${headquartersName}%'
and startTime>=#{startTime}
and endTime<=#{endTime}
and ss.ProjectNames like '%${projectName}%'
and sc.companyCode like '%${companyCode}%'
ORDER BY sc.CreateTime DESC LIMIT #{pageIndex},#{pageSize}

SELECT COUNT(1) AS total FROM s_company_info sc left join b_account sa on sa.ID=sc.Creator left join (select GROUP_CONCAT(bp.ProjectName) as ProjectNames, GROUP_CONCAT(org.orgName) AS orgName, GROUP_CONCAT(org.orgType) AS orgType, org.OrgCompanyID from s_organization org inner join b_project bp on bp.ID=org.ProjectID and OrgCompanyID is not null GROUP BY OrgCompanyID) ss on ss.OrgCompanyID=sc.ID WHERE sc.IsDel = 0
AND sc.AuthCompanyID=#{AuthCompanyID}
and sc.companyName like '%${companyName}%'
and sc.headquartersName like '%${headquartersName}%'
and startTime>#{startTime}
and endTime<#{endTime}
and ss.ProjectNames like '%${projectName}%'
and sc.companyCode like '%${companyCode}%'
ORDER BY sc.CreateTime DESC
